import React from "react";

class MarvelMovie extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            error:null,
            isLoaded: false,
            marvelMovies: []
        }
    }


    componentDidMount() {
      fetch('http://localhost:3091/marvelAPI')
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            marvelMovies: result.marvelMovies
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }



    render() {
        const { err, isLoaded, marvelMovies } = this.state
        if (err) {
            return <div>Error {err.message}</div>
        }
        else if (!isLoaded) {
            return <div>Loading...</div>
        }

        console.log(marvelMovies)

    return (
        <div></div>
    )
  }
}    
export default MarvelMovie
