import React from "react";
import Projectnav from "./Projectnav";

class Calculator extends React.Component {

    constructor() {
        super()
        this.state = {
            currInput : "0",
            secondInput : "",
            calcHistory : ""
        }
      }
      onCalcButtonPress = (e) => {
        let calcButtonValue = e.target.value
        let newInput = `${this.state.currInput}` + calcButtonValue - '0';  
        let secondInput = this.state.currInput;
        e.preventDefault()
        switch(calcButtonValue) {
            case '1':
            case '2':
            case '3':
            case '4':
            case '5':        
            case '6':
            case '7':
            case '8':
            case '9':
            case '0':
            case '.':    

                this.setState ({
                      currInput : newInput,

                  })

                break;
            case '+':
            case '-':
            case '/':
            case '*':
                console.log(this.state.currInput);
                this.setState ({
                    currInput : newInput
                })
                break;
            case 'AC': 
                this.setState ({
                    currInput : '0'
                })  
            break;  
        }

      }
    render() {
        return (
        <div className='calcHolder'>
            <Projectnav></Projectnav>
            <h1>Simple Calculator</h1>
            <div className='calcBody'>
            <br />
                <div className='calcDisplay'>
                    <p>{this.state.currInput}</p>
                    <p>{this.state.secondInput}</p>
                </div>
                    <div className='calcButtons'>
                        <button value ='1' className='calcButton' onClick={this.onCalcButtonPress}>1</button>
                        <button value ='2' className='calcButton' onClick={this.onCalcButtonPress}>2</button>
                        <button value ='3' className='calcButton' onClick={this.onCalcButtonPress}>3</button>
                        <button value ='+' className='calcButton' onClick={this.onCalcButtonPress}>+</button>
                        <br />
                        <button value ='4' className='calcButton' onClick={this.onCalcButtonPress}>4</button>
                        <button value ='5' className='calcButton' onClick={this.onCalcButtonPress}>5</button>
                        <button value ='6' className='calcButton' onClick={this.onCalcButtonPress}>6</button>
                        <button value ='-' className='calcButton' onClick={this.onCalcButtonPress} >-</button>
                        <br />
                        <button value ='7' className='calcButton' onClick={this.onCalcButtonPress}>7</button>
                        <button value ='8' className='calcButton' onClick={this.onCalcButtonPress}>8</button>
                        <button value ='9' className='calcButton' onClick={this.onCalcButtonPress}>9</button>
                        <button value ='/' className='calcButton' onClick={this.onCalcButtonPress}>&divide;</button>
                        <br />
                        <button value ='.' className='calcButton' onClick={this.onCalcButtonPress}>.</button>
                        <button value ='0' className='calcButton' onClick={this.onCalcButtonPress}>0</button>
                        <button value ='AC' className='calcButton' onClick={this.onCalcButtonPress}>AC</button>
                        <button value ='*' className='calcButton' onClick={this.onCalcButtonPress}>*</button>
                        <br />
                        <button value ='C' className='longBTN calcButton'>C</button>
                        <button value ='=' className='longBTN calcButton'>=</button>
                    </div>
                </div>
            </div>

        )
    }


}

export default Calculator;
