    constructor(props) {
        super(props)
        this.state = {
            error:null,
            isLoaded: false,
            marvelMovieAPI: []
        }
    }


    componentDidMount() {
      fetch('http://localhost:3090/marvelAPI')
        .then((res) => res.json())
        .then((result) => this.setState({
            isLoaded:true,
            marvelMovieAPI: result.MarvelMovies
        }))

        .catch( err => {
            this.setState({
                isLoaded:true,
                err
