import fetch from "node-fetch";
import React from "react";
import Projectnav from "./Projectnav";

class CookingApp extends React.Component {

  constructor() {
    super()
    this.state = {
      error: null,
      isLoaded: false,
      recipeData : [],
      query : ''
    }
  }
fetchRecipe(ing) {
  ing = document.querySelector('ingridentInput').value
 return    fetch(`http://localhost:3093/foodAPI?ingredients=${ing}`)
 .then(res => res.json())
   .then(
     (result) => {
       this.setState({
         isLoaded: true,
         recipeData : result
       })
       console.log(result);
     },
     // Note: it's important to handle errors here
     // instead of a catch() block so that we don't swallow
     // exceptions from actual bugs in components.
     (error) => {
       this.setState({
         isLoaded: true,
         error
       });
     }
   )
}




  render() {
    return (

      <div className="recipieAppHolder">
        <Projectnav></Projectnav>

        <h1>What I got in the fridge</h1>
        <h2>Enter ingredients here</h2>
        <input className="ingredientInput"></input>
        <div className="searchButtonHolder">
          <button className="cookingSearchButton" onClick={this.fetchRecipe}>Search for recipies</button>
        </div>
      </div>
    );
  }
}

export default CookingApp;
