import React from "react";
import Projectnav from "./Projectnav";

class Calculator extends React.Component {

    constructor() {
        super()
        this.state = {
            currInput : "0",
            history : ""
        }
      }
      onCalcButtonPress = (e) => {
        e.preventDefault()  
        this.setState ({
              currInput : e.target.value 
          })
      }
    render() {
        return (
        <div className='calcHolder'>
            <Projectnav></Projectnav>
            <h1>Simple Calculator</h1>
            <div className='calcBody'>
            <br />
                <div className='calcDisplay'>
                    <p>{this.state.currInput}</p>
                </div>
                    <div className='calcButtons'>
                        <button value ='1' className='calcButton' onClick={this.onCalcButtonPress}>1</button>
                        <button value ='2' className='calcButton'>2</button>
                        <button value ='3' className='calcButton'>3</button>
                        <button value ='+' className='calcButton'>+</button>
                        <br />
                        <button value ='4' className='calcButton'>4</button>
                        <button value ='5' className='calcButton'>5</button>
                        <button value ='6' className='calcButton'>6</button>
                        <button value ='-'className='calcButton'>-</button>
                        <br />
                        <button value ='7' className='calcButton'>7</button>
                        <button value ='8' className='calcButton'>8</button>
                        <button value ='9' className='calcButton'>9</button>
                        <button value ='/' className='calcButton'>&divide;</button>
                        <br />
                        <button value ='.' className='calcButton'>.</button>
                        <button value ='0' className='calcButton'>0</button>
                        <button value ='AC' className='calcButton'>AC</button>
                        <button value ='*' className='calcButton'>*</button>
                        <br />
                        <button value ='C' className='longBTN calcButton'>C</button>
                        <button value ='=' className='longBTN calcButton'>=</button>
                    </div>
                </div>
            </div>

        )
    }


}

export default Calculator;
